<?php session_start();require_once __DIR__ . '/../classes/Database.php';require_once __DIR__ . '/../classes/User.php';$error = "";if ($_SERVER['REQUEST_METHOD'] === 'POST') {$email = isset($_POST['email']) ? htmlspecialchars($_POST['email']) : null;$password = isset($_POST['password']) ? htmlspecialchars($_POST['password']) : null;if ($email && $password) {$db = (new Database())->getConnection();$user = new User($db);$login_user = $user->login($email, $password);if ($login_user) {$_SESSION['user_id'] = $login_user['id'];$_SESSION['username'] = $login_user['username'];header('Location: admin.php');exit();} else {$error = "Email ou mot de passe incorrect.";  } } else {$error = "Veuillez entrer un email et un mot de passe.";}}?><!DOCTYPE html><html lang="fr"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><link rel="stylesheet" href="auth.css"><title>Connexion</title></head><body><?php include '../header.php'; ?><h1>Connexion</h1><?php if ($error): ?><p style="color: red; text-align: center;"><?= htmlspecialchars($error); ?></p><?php endif; ?><form method="POST" action="auth.php" style="text-align: center;"><div><label for="email">Email :</label><br><input type="email" id="email" name="email" required></div><br><div><label for="password">Mot de passe :</label><br><input type="password" id="password" name="password" required></div><br><button type="submit" style="padding: 10px 20px; background-color: #1cbac9; color: #fff; border: none; border-radius: 5px; cursor: pointer;"></button></form></body></html>   